/*******************************
 * CONFIGURACIÓN GENERAL
 *******************************/
const dominio_evolution = "http://localhost:8080";

/*******************************
 * WHITELIST DE NÚMEROS
 *******************************/
const numeros_admitidos = [
  "593997350897",
  "593980062977",
  "593978640544",
  "593997129254",
  "593995817315",
  "593988207600"
];

/*******************************
 * FUNCIÓN WHITELIST
 *******************************/
function comprobar_whitelist(numero, evolutional) {
  if (!numero) return false;

  // Limpiar número si viene de evolution (quita el @s.whatsapp.net)
  let numero_limpio = numero;
  if (evolutional && typeof numero === "string") {
    numero_limpio = numero.split("@")[0];
  }

  // Si es Evolution y el mensaje es enviado por el bot (fromMe), cortamos flujo
  if (evolutional && $input.first().json.body?.data?.key?.fromMe) {
    return null; // señal explícita de corte (mensaje propio)
  }

  return numeros_admitidos.includes(numero_limpio);
}

/*******************************
 * OBJETO BASE
 *******************************/
let datos = {
  dominio_evolution
};

/*******************************
 * 1. TRIGGER DESDE CONSOLA
 *******************************/
if ($input.first().json.action) {
  datos.tipo_trigger = "consola";
  datos.numero_cuenta = "Consola";
  datos.nombre_contacto = "Admin";
  datos.mensaje_texto = $input.first().json.chatInput;

  return datos;
}

/*******************************
 * 2. TRIGGER DESDE EVOLUTION API
 *******************************/
if ($input.first().json.body?.data?.messageType) {
  const body = $input.first().json.body;

  datos.tipo_trigger = "evolution";

  const permitido = comprobar_whitelist(
    body.data.key.remoteJid,
    true
  );

  // Cortar flujo si es mensaje propio del bot o no permitido (según lógica deseada)
  if (permitido === null) return []; // Es mensaje propio

  // Opcional: Si quieres bloquear números no admitidos en Evolution descomenta esto:
  // if (!permitido) return []; 

  datos.is_testing = permitido;
  datos.instancia = body.instance;
  datos.numero_cuenta = body.data.key.remoteJid;

  // SOLUCIÓN NOMBRE NULL O NUMÉRICO
  const pushName = body.data.pushName;
  datos.nombre_contacto =
    pushName && isNaN(pushName) ? pushName : "Cliente";

  datos.id_mensaje = body.data.key.id;
  datos.tipo_mensaje = body.data.messageType;

  // Recuperar texto (sea texto puro o caption de imagen)
  datos.mensaje_texto =
    body.data.message.conversation ||
    body.data.message.extendedTextMessage?.text || 
    body.data.message.imageMessage?.caption ||
    "";

  return datos;
}

/*******************************
 * 3. TRIGGER DESDE WHATSAPP API OFICIAL (META)
 * (Este es el bloque que faltaba)
 *******************************/
if ($input.first().json.messaging_product === "whatsapp") {

  datos.tipo_trigger = "oficial";

  // Meta suele enviar arrays, tomamos el primero
  const msg = $input.first().json.messages?.[0];
  const contact = $input.first().json.contacts?.[0];

  // Si no hay mensaje o contacto, cortamos
  if (!msg || !contact) return [];

  // Verificamos whitelist (False porque no es evolution)
  const permitido = comprobar_whitelist(contact.wa_id, false);

  // AQUÍ DECIDES: ¿Si no está en whitelist cortas el flujo?
  // Asumiremos que si no está permitido, retornamos vacío para no procesar.
  // Si quieres que pase aunque no esté en la lista, borra la linea de abajo.
  if (!permitido) return []; 

  datos.is_testing = permitido;
  datos.numero_cuenta = contact.wa_id;
  datos.id_mensaje = msg.id;
  datos.nombre_contacto = contact.profile?.name || "Desconocido";

  // Normalizar tipo de mensaje
  if (msg.type === "text") {
    datos.tipo_mensaje = "conversation"; // Para mantener consistencia con Evolution
    datos.mensaje_texto = msg.text.body;
  } else if (msg.type === "image") {
    datos.tipo_mensaje = "imageMessage";
    datos.mensaje_texto = msg.image.caption || "";
    datos.imagen = {
        id: msg.image.id,
        mime: msg.image.mime_type
    };
  } else {
      datos.tipo_mensaje = msg.type;
      datos.mensaje_texto = "";
  }

  return datos;
}

/*******************************
 * SI NO COINCIDE NINGÚN TRIGGER
 *******************************/
return [];

var mensajes = {
    "readMessages" : []
}

// 1. Obtenemos la lista original
const raw_ids = $input.first().json.IDMensaje;

// 2. EL TRUCO: Usamos 'Set' para eliminar duplicados automáticamente
// (Un Set es una lista que no permite valores repetidos)
const id_mensajes = [...new Set(raw_ids)].reverse();

const remoteJid = $('DataCleaning 1').first().json.numero_cuenta;

// 3. Ahora recorremos solo la lista limpia
for (const id_mensaje of id_mensajes) {
    mensajes.readMessages.push({
        "remoteJid": remoteJid,
        "fromMe": false,
        "id": id_mensaje
    });
}

return [ { json : mensajes } ]

// 1. EL TRUCO PARA EVITAR LA PÉRDIDA DE DATOS EN REDIS
// En lugar de leer del nodo anterior, leemos directo del origen:
let input = {};
try {
    input = $('Matematicas').first().json;
} catch (e) {
    input = $input.first().json || {};
}

const mensaje_usuario = $('DataCleaning 1').first().json.mensaje_texto || "";

const es_url = mensaje_usuario.includes("http");
const es_imagen = input.es_analisis_imagen === true;
let precio_num = parseFloat((input.precio_web || "0").toString().replace(/[$,]/g, ""));
const tiene_precio = !isNaN(precio_num) && precio_num > 0.01;

// DETECCIÓN DE INTENCIÓN DE PAGO (Checkout)
const quiere_pagar = /no|pagar|finalizar|listo|comprar|terminar/i.test(mensaje_usuario) && !es_url && !es_imagen;

let prompt_dinamico = "";

if (tiene_precio) {
    let texto_colores = "";
    if (es_imagen && input.colores && input.colores.length > 0) {
        texto_colores = `He analizado que este producto cuenta con la siguiente disponibilidad de opciones: ${input.colores.join(", ")}. ¿Cuál prefieres para tu orden?\n\n`;
    }

    let seccion_carrito = "";
    if (input.cantidad_productos > 1) {
        let lista_resumen = input.carrito.map(item => `* ${item.nombre.substring(0,40)}... - $${item.total.toFixed(2)}`).join("\n    ");
        seccion_carrito = `
    -------------------------------------------
    (SECCIÓN DEL CARRITO)
    * **Productos en carrito:**
    ${lista_resumen}
    * **TOTAL DE LOS PRODUCTOS A PAGAR:** $${input.gran_total}
    -------------------------------------------`;
    }

    prompt_dinamico = `
    MODO: COTIZACIÓN EXITOSA

    INSTRUCCIÓN ESTRICTA: Copia el siguiente texto EXACTAMENTE como está.

    PLANTILLA OBLIGATORIA:
    ¡Hola! Soy Estella, tu asistente de importaciones en Temu.

    **Desglose financiero:**

    * **Producto:** ${input.producto}
    * **Precio Web:** $${input.precio_web}

    -------------------------------------------
    * **Subtotal Acumulado:** $${input.precio_web}
    * **Impuesto Estimado:** $${input.impuesto_estimado}
    * **TOTAL FINAL A PAGAR:** $${input.total_final_a_pagar}
    ${seccion_carrito}

    ${texto_colores}¿Desea agregar algún otro producto al carrito o prefiere finalizar la orden?
    `;

} else if (quiere_pagar && input.cantidad_productos > 0) {
    let lista_final = input.carrito.map(item => `* ${item.nombre.substring(0,50)}... - $${item.total.toFixed(2)}`).join("\n    ");
    prompt_dinamico = `
    MODO: FINALIZAR ORDEN (CHECKOUT)

    INSTRUCCIÓN ESTRICTA: El usuario ya no quiere agregar más cosas. Muestra el carrito final. Copia la plantilla.

    PLANTILLA OBLIGATORIA:
    ¡Excelente! Aquí tienes el resumen final de tu carrito de compras:

    ${lista_final}

    -------------------------------------------
    * **TOTAL DE LOS PRODUCTOS A PAGAR:** $${input.gran_total}
    -------------------------------------------

    Para proceder con el pago y la importación, por favor confírmame tu método de pago preferido.
    `;

} else if (es_url && !tiene_precio) {
    prompt_dinamico = `MODO: PEDIR CAPTURA\nEl link fue detectado pero no el precio. Saluda como Estella y pide captura de pantalla.`;
} else if (es_imagen && !tiene_precio) {
    prompt_dinamico = `MODO: FALLO VISUAL\nRecibí una imagen pero no pude leer el precio. Saluda como Estella y pide el precio manual.`;
} else {
    prompt_dinamico = `MODO: CONVERSACIÓN\nInicia tu mensaje con "¡Hola! Soy Estella..." y responde al saludo.`;
}

return {
    prompt_dinamico,
    producto: input.producto || "Producto",
    precio_web: precio_num.toFixed(2),
    total_final_a_pagar: input.total_final_a_pagar || "0"
};

let precioBase = 0;
let nombre = "Producto";
let colores_detectados = [];
let es_imagen = false;

try {
    const dataImg = $('Limpiar Imagen').first().json;
    if (dataImg) {
        precioBase = parseFloat(dataImg.precio_detectado || 0);
        nombre = dataImg.producto || "Producto";
        colores_detectados = dataImg.colores || [];
        es_imagen = true;
    }
} catch (e) {}

if (precioBase >= 592.9 && precioBase <= 593.1) precioBase = 0;

// 2. RECUPERAR EL CARRITO HISTÓRICO (Desde Redis)
let carrito = [];
try {
    // Busca si ya hay un carrito guardado en la memoria
    const redisData = $('Conseguir Carrito').first().json; 
    if (redisData && redisData.carrito_json) {
        carrito = JSON.parse(redisData.carrito_json);
    }
} catch (e) {}

// 3. CÁLCULOS DEL ITEM ACTUAL
let impuestos = 0;
let total_item = 0;

if (precioBase > 0) {
    impuestos = 3.00;
    if (precioBase > 50) impuestos += (precioBase * 0.15); // Lógica de impuestos
    total_item = precioBase + impuestos;

    // Metemos el producto nuevo a la lista del carrito
    carrito.push({
        nombre: nombre,
        precio: precioBase,
        impuesto: impuestos,
        total: total_item
    });
}

// 4. CALCULAR EL GRAN TOTAL SUMANDO TODA LA LISTA
let gran_total = carrito.reduce((sum, item) => sum + item.total, 0);

return {
    producto: nombre,
    precio_web: precioBase.toFixed(2),
    impuesto_estimado: impuestos.toFixed(2),
    total_final_a_pagar: total_item.toFixed(2),
    hay_precio: precioBase > 0,
    colores: colores_detectados,
    es_analisis_imagen: es_imagen,

    // VARIABLES DEL CARRITO
    carrito: carrito,
    cantidad_productos: carrito.length,
    gran_total: gran_total.toFixed(2),
    carrito_json_string: JSON.stringify(carrito) // Esto es vital para guardarlo en Redis luego
};

const cleaning = $('DataCleaning 1').first().json;
const redis_msjs = $input.first().json.mensajes || [];

const historial_limpio = redis_msjs.reverse().map(m => {
    return m.replace(/593\\d+/g, "[NÚMERO]");
}).join('\n');

// EXTRA: Detectar si el mensaje actual tiene un link antes de concatenar
const mensaje_actual = cleaning.mensaje_texto || "";
const tiene_link = mensaje_actual.toLowerCase().includes("http");

return {
    nombre_usuario: cleaning.nombre_contacto,
    mensaje_puro: mensaje_actual, // Solo el texto que acaba de enviar
    mensajes_usuario: `Nombre del Cliente: ${cleaning.nombre_contacto}\n\nHistorial:\n${historial_limpio}`,
    contiene_url: tiene_link // Booleano simple para el IF
};

// 1. Obtenemos el texto crudo de la IA
const response = $input.first().json.content || "";
const cleanJson = response.replace(/```json\n?|\n?```/g, "").trim();

// Valores por defecto
let producto = "Producto Detectado";
let precio = "0";
let colores = [];

try {
    // INTENTO 1: ¿El modelo obedeció y envió un JSON válido?
    const data = JSON.parse(cleanJson);
    producto = data.nombre_producto || producto;
    precio = data.precio_detectado || precio;
    colores = data.colores_disponibles || colores;

} catch (e) {
    // INTENTO 2: El modelo falló y envió texto normal (Markdown).
    // Usamos Expresiones Regulares para "cazar" los datos en el texto.

    // Buscar Nombre (Asume que está en la primera línea entre **)
    const matchNombre = response.match(/^\*\*(.*?)\*\*/m) || response.match(/(?:nombre_producto|Producto).*?:?\s*\**([^\n]+)\**/i);
    if (matchNombre) producto = matchNombre[1].replace(/\*\*/g, "").trim();

    // Buscar Precio (Busca el número después de "Precio Detectado")
    const matchPrecio = response.match(/(?:Precio Detectado|precio_detectado)[^0-9]*([0-9]+[.,][0-9]+)/i);
    if (matchPrecio) precio = matchPrecio[1].trim();

    // Buscar Colores (Busca lo que sigue después de "Colores Disponibles")
    const matchColores = response.match(/(?:Colores Disponibles|colores_disponibles)[^:]*:\s*\**([^\n]+)/i);
    if (matchColores) {
        let coloresStr = matchColores[1].replace(/\*\*/g, "").trim();
        // Separamos los colores por comas y limpiamos espacios
        colores = coloresStr.split(",").map(c => c.trim());
    }
}

// 3. Limpieza final del precio para las Matemáticas
let precio_limpio = precio
    .toString()
    .replace("$", "")
    .replace(",", ".") // Convierte 24,57 a 24.57
    .trim();

return {
    producto: producto,
    precio_detectado: precio_limpio,
    colores: colores,
    es_analisis_imagen: true
};

const item = items[0].json;
const binaryData = items[0].binary;

// El nodo HTTP anterior debe devolver un campo 'base64'
// A veces Evolution lo devuelve dentro de 'data', ajusta si es necesario: item.data?.base64
const base64Raw = item.base64 || item.data?.base64;

if (base64Raw) {
  // Limpiamos cabeceras si existen
  const base64Clean = base64Raw.replace(/^data:image\/[a-z]+;base64,/, "");

  return [
    {
      json: item,
      binary: {
        data: {
          data: base64Clean,
          mimeType: 'image/jpeg', // Puedes inferirlo o dejarlo fijo
          fileExtension: 'jpg',
          fileName: 'imagen_procesada.jpg'
        }
      }
    }
  ];
}

return items;
